exports.list = function(req, res) {
  var array = ['sectionA', 'sectionB'];
  var i;
  for(i = 0; i < array.length; i++) {
    Issue.count({ 'section.name': array[i]}, function(err, count) {
      console.log('Section name: ' + array[i] + ' number of employees: ' + count);
      });
    i++;
  }
}
