using namespace std;

// Node structure
struct Node {
    Node* left;
    Node* right;
    int value;
};

// Function to mirror a tree
void mirrorTree(Node*& root) {
    if (root == nullptr) return; // Base case: if the node is null, do nothing

    // Swap the left and right children
    swap(root->left, root->right);

    // Recursively mirror the left and right subtrees
    mirrorTree(root->left);
    mirrorTree(root->right);
}

// Helper function to print the tree in inorder traversal
void printInorder(Node* root) {
    if (root == nullptr) return;
    printInorder(root->left);
    cout << root->value << " ";
    printInorder(root->right);
}

int main() {
    // Construct the original tree
    Node* root = new Node();
    root->value = 1;
    root->left = new Node();
    root->left->value = 2;
    root->right = new Node();
    root->right->value = 3;
    root->right->left = new Node();
    root->right->left->value = 4;
    root->right->right = new Node();
    root->right->right->value = 5;

    // Mirror the tree
    mirrorTree(root);

    // Print the mirrored tree
    cout << "Mirrored tree in inorder traversal: ";
    printInorder(root);
    cout << endl;

    return 0;
}
